{
 "Version": "2012-10-17",
 "Statement": [
 {
 "Sid": "DenyOutsideIreland",
 "Effect": "Deny",
 "NotAction": [
 "s3:*",
 "route53:*",
 "iam:*",
 "cloudfront:*"
 ],
 "Resource": "*",
 "Condition": {
 "StringNotEquals": {
 "aws:RequestedRegion": "eu-west-1"
 }
 }
 },
 {
 "Sid": "AllowSelectServicesInIreland",
 "Effect": "Allow",
 "Action": [
 "s3:*",
 "route53:*",
 "iam:*",
 "cloudfront:*"
 ],
 "Resource": "*",
 "Condition": {
 "StringEquals": {
 "aws:RequestedRegion": "eu-west-1"
 }
 }
 }
 ]
}
