{
 "type": "Microsoft.Storage/storageAccounts/blobServices/lifecyclePolicies",
 "name": "storageAccount/default/lifecyclePolicies",
 "apiVersion": "2019-06-01",
 "properties": {
 "policy": {
 "rules": [
 {
 "name": "rule1",
 "enabled": true,
 "type": "Lifecycle",
 "definition": {
 "actions": {
 "baseBlob": {
 "tierToArchive": "90"
 },
 "snapshot": {
 "deleteSnapshots": "true"
 }
 },
 "filters": {
 "blobTypes": [
 "blockBlob"
 ],
 "prefixMatch": [
 "container1/"
 ]
 }
 }
 }
 ]
 }
 }
}
