The answer is correct (0,xxx-xx,5), and here is an example of it 
--Create table
Create Table Customers
(ID int Primary Key identity(1,1) Not Null, Phone Nvarchar(100))
GO
--Load Data
insert into Customers
Values
('555-555-0173'),('555-505-3124'),('555-689-4321')
--Create user
CREATE USER TestUser WITHOUT LOGIN; 
GRANT SELECT ON Customers TO TestUser;
-- Test the User in Select data
EXECUTE AS USER = 'TestUser'; 
SELECT * FROM Customers; 
REVERT; 
---Now add Masking
ALTER TABLE Customers 
ALTER COLUMN Phone nvarchar(100) MASKED WITH (FUNCTION= 'partial(0,"xxx-xxx",5)');
--Select the data again using Testuser
EXECUTE AS USER = 'TestUser'; 
SELECT * FROM Customers; 
REVERT;
