static void TestD35ExamQuestion(Args _args)
{
 TmpFrmVirtual tmpFrmVirtual;
 int salesQty = 5;


 void updateValues(TmpFrmVirtual _tmpFrmVirtual, str _salesQty)
 {
 TmpFrmVirtual tmpLocalFrmVirtual = _tmpFrmVirtual;
 int localSalesQty = str2int(_salesQty);

 tmpLocalFrmVirtual.Id = 'SID1234-Updated';
 localSalesQty = 10;
 } 

 void run()
 {
 tmpFrmVirtual.Id = 'SID1234';

 // Call the updateValues method 
 updateValues(tmpFrmVirtual, int2str(salesQty));

 info(tmpFrmVirtual.Id);
 info(strFmt('%1',salesQty));
 } 

 // Execute the run method
 run(); 
}

