C: show all products 
set SERVEROUTPUT ON
declare 
inc number := .01;
cursor pdt is
 select proceso as prodname,(precio*inc) aumento from producto;
begin
 for pdt_rec in pdt loop
 dbms_output.put_line('prod name '||pdt_rec.prodname|| ' price increm ' ||
 pdt_rec.aumento );
 inc := inc +.01;
end loop;
end;


