public class Planet {

 public String name;
 public int moons;

 public Planet (String name, int moons) {
 this.name = name;
 this.moons = moons;
 }

 public static void main(String[] args) {

 Planet[] planets = {
 new Planet("Mercury",0),
 new Planet("Venus",0),
 new Planet("Earth",1),
 new Planet("Mars",2)
 };

