package q34;

public class Test {

 public void sum(int a, int b) {
 System.out.print(" A");
 }

 public void sum(int a, float b) {
 System.out.print(" B");
 }

 public void sum(float a, float b) {
 System.out.print(" C");
 }

 public void sum(double... a) {
 System.out.print(" D");
 }

 public static void main(String[] args) {
 Test t = new Test();
 t.sum(10, 15.25);
 t.sum(10, 24);
 t.sum(10.25, 10.25);
 }
}

// Result:
// D A D

