import java.util.List;

public class Q38 {

 public String attach1 ( List<String> data) {
 return data.parallelStream().reduce("w", (n,m) -> n+m, String ::concat);
 }

 public String attach2 ( List<String> data) {
 return data.parallelStream().reduce( (l,p) -> l + p).get();
 }


